import React, { useState, useRef, useEffect } from 'react';
import { 
  Typography, TextField, Button, Box, 
  CircularProgress, Avatar, ThemeProvider, 
  createTheme, CssBaseline, Container, Paper
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PsychologyIcon from '@mui/icons-material/Psychology';
import PersonIcon from '@mui/icons-material/Person';
import ReactMarkdown from 'react-markdown';
import axios from 'axios';
import './App.css';

// Create a theme inspired by ChatGPT's clean design
const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#10a37f',
      light: '#1dd1a1',
      dark: '#0b8a6c',
    },
    secondary: {
      main: '#19c37d',
      light: '#2dd4a0',
      dark: '#0d8a5a',
    },
    background: {
      default: '#343541',
      paper: '#444654',
    },
    text: {
      primary: '#ececf1',
      secondary: '#d1d5db',
    },
    error: {
      main: '#ef4444',
    },
    success: {
      main: '#19c37d',
    },
  },
  typography: {
    fontFamily: '"S√∂hne", "Helvetica Neue", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    subtitle1: {
      fontWeight: 400,
    },
    button: {
      fontWeight: 500,
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 4,
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
          '&:hover': {
            boxShadow: 'none',
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
          },
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
          backgroundColor: '#444654',
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            backgroundColor: '#40414f',
            '& fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.1)',
            },
            '&:hover fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.2)',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#19c37d',
            },
          },
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: '#343541',
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-track': {
            background: '#40414f',
          },
          '&::-webkit-scrollbar-thumb': {
            background: '#565869',
            borderRadius: '4px',
          },
        },
      },
    },
  },
});

function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Sample welcome message
  useEffect(() => {
    setMessages([
      {
        text: "Hi there! I'm TheraPeek, your mental health chat companion. How are you feeling today?",
        sender: 'bot',
        timestamp: new Date(),
      },
    ]);
  }, []);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Focus input on load
  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message to chat
    const userMessage = {
      text: input,
      sender: 'user',
      timestamp: new Date(),
    };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);
    setError(null);

    try {
      // Call API
      const response = await axios.post('http://localhost:5000/api/chat', {
        message: input,
        max_length: 100,
        temperature: 0.7,
      });

      // Add bot response to chat
      const botMessage = {
        text: response.data.response || "I'm not sure how to respond to that.",
        sender: 'bot',
        timestamp: new Date(),
        processingTime: response.data.processing_time,
      };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error('Error communicating with the API:', err);
      setError('Sorry, I had trouble connecting. Please try again.');

      // Add error message to chat
      setMessages((prev) => [
        ...prev,
        {
          text: 'Sorry, I had trouble connecting. Please try again.',
          sender: 'bot',
          timestamp: new Date(),
          isError: true,
        },
      ]);
    } finally {
      setLoading(false);
      // Focus back on input
      inputRef.current?.focus();
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className="app-container">
        <div className="chat-container">
          <div className="messages-container">
            {messages.map((msg, index) => (
              <div 
                key={index}
                className={`message-wrapper ${msg.sender === 'bot' ? 'bot-message' : 'user-message'}`}
              >
                <div className="message-content">
                  <div className="message-avatar">
                    {msg.sender === 'user' ? 
                      <Avatar sx={{ bgcolor: theme.palette.secondary.main }}>
                        <PersonIcon fontSize="small" />
                      </Avatar> : 
                      <Avatar sx={{ bgcolor: theme.palette.primary.main }}>
                        <PsychologyIcon fontSize="small" />
                      </Avatar>
                    }
                  </div>
                  <div className="message-bubble">
                    <div className="message-header">
                      <Typography variant="subtitle2" component="span" className="message-sender">
                        {msg.sender === 'user' ? 'You' : 'TheraPeek'}
                      </Typography>
                      <Typography variant="caption" component="span" className="message-time">
                        {formatTime(msg.timestamp)}
                      </Typography>
                    </div>
                    <div className={`message-text ${msg.isError ? 'error-message' : ''}`}>
                      <ReactMarkdown>{msg.text}</ReactMarkdown>
                    </div>
                    {msg.processingTime && (
                      <Typography variant="caption" className="processing-time">
                        Response time: {msg.processingTime.toFixed(2)}ms
                      </Typography>
                    )}
                    {msg.sender === 'bot' && !msg.isError && (
                      <div className="message-feedback">
                        <button className="feedback-button" title="Helpful">
                          <span role="img" aria-label="thumbs up">üëç</span>
                        </button>
                        <button className="feedback-button" title="Not helpful">
                          <span role="img" aria-label="thumbs down">üëé</span>
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
            {loading && (
              <div className="message-wrapper bot-message">
                <div className="message-content">
                  <div className="message-avatar">
                    <Avatar sx={{ bgcolor: theme.palette.primary.main }}>
                      <PsychologyIcon fontSize="small" />
                    </Avatar>
                  </div>
                  <div className="message-bubble">
                    <div className="message-header">
                      <Typography variant="subtitle2" component="span" className="message-sender">
                        TheraPeek
                      </Typography>
                    </div>
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          <form onSubmit={handleSubmit} className="input-container">
            <TextField
              inputRef={inputRef}
              fullWidth
              variant="outlined"
              placeholder="Message TheraPeek..."
              value={input}
              onChange={handleInputChange}
              disabled={loading}
              InputProps={{
                endAdornment: (
                  <Button 
                    type="submit"
                    disabled={!input.trim() || loading}
                    color="primary"
                    variant="contained"
                    className="send-button"
                  >
                    {loading ? <CircularProgress size={24} color="inherit" /> : <SendIcon />}
                  </Button>
                ),
              }}
            />
          </form>

          <div className="footer">
            <Typography variant="caption" color="text.secondary">
              TheraPeek - Gen Z Mental Health Chatbot
            </Typography>
          </div>
        </div>
      </div>
    </ThemeProvider>
  );
}

export default App;
