import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [typing, setTyping] = useState(false);
  const messagesEndRef = useRef(null);

  // Fake “read receipt” logic (for user messages)
  const [showReadReceipt, setShowReadReceipt] = useState(false);

  useEffect(() => {
    // Example: Add an initial bot message and “today/time” 
    setMessages([
      { id: 1, sender: 'system', text: `Today 7:16 pm`, timestamp: null },
      { id: 2, sender: 'bot', text: `Hello! How can I help you?`, timestamp: '7:16 pm' },
    ]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSend = (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const timeNow = new Date().toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' });
    const newMsg = {
      id: Date.now(),
      sender: 'user',
      text: input.trim(),
      timestamp: timeNow,
    };

    setMessages((prev) => [...prev, newMsg]);
    setInput('');
    setShowReadReceipt(false);

    // Simulate bot “typing” for 1.5s, then respond
    setTyping(true);
    setTimeout(() => {
      setTyping(false);
      setShowReadReceipt(true);
      const botReply = {
        id: Date.now() + 1,
        sender: 'bot',
        text: 'This is a bot reply!',
        timestamp: new Date().toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' }),
      };
      setMessages((prev) => [...prev, botReply]);
    }, 1500);
  };

  return (
    <div className="app-container">
      <div className="chat-container">
        <div className="messages-container">
          {messages.map((msg) => {
            if (msg.sender === 'system') {
              // “System” message for date/time in the middle
              return (
                <div key={msg.id} style={{ textAlign: 'center', margin: '8px 0', color: '#8e8e93' }}>
                  {msg.text}
                </div>
              );
            }

            return (
              <div
                key={msg.id}
                className={`message-wrapper ${msg.sender}-message`}
              >
                <div className="message-content">
                  <div>{msg.text}</div>
                  {msg.timestamp && (
                    <span className="message-time">{msg.timestamp}</span>
                  )}
                  {msg.sender === 'user' && showReadReceipt && (
                    <span className="read-receipt">Read {msg.timestamp}</span>
                  )}
                </div>
              </div>
            );
          })}

          {typing && (
            <div className="message-wrapper bot-message">
              <div className="typing-indicator-bubble">
                <div className="typing-dots">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>

        <div className="input-container">
          <form onSubmit={handleSend}>
            <input
              type="text"
              placeholder="iMessage"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button className="send-button" type="submit">
              ➤
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
