import React, { useState, useRef, useEffect } from 'react';
import { 
  Typography, TextField, Button, Box, 
  CircularProgress, Avatar, ThemeProvider, 
  createTheme, CssBaseline, Container, Paper
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PsychologyIcon from '@mui/icons-material/Psychology';
import PersonIcon from '@mui/icons-material/Person';
import ReactMarkdown from 'react-markdown';
import axios from 'axios';
import './App.css';

// Create a theme inspired by ChatGPT's clean design
const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#10a37f',
      light: '#1dd1a1',
      dark: '#0b8a6c',
    },
    secondary: {
      main: '#19c37d',
      light: '#2dd4a0',
      dark: '#0d8a5a',
    },
    background: {
      default: '#343541',
      paper: '#444654',
    },
    text: {
      primary: '#ececf1',
      secondary: '#d1d5db',
    },
    error: {
      main: '#ef4444',
    },
    success: {
      main: '#19c37d',
    },
  },
  typography: {
    fontFamily: '"SÃ¶hne", "Helvetica Neue", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    subtitle1: {
      fontWeight: 400,
    },
    button: {
      fontWeight: 500,
      textTransform: 'none',
    },
  },
  shape: {
    borderRadius: 4,
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
          '&:hover': {
            boxShadow: 'none',
            backgroundColor: 'rgba(255, 255, 255, 0.1)',
          },
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
          backgroundColor: '#444654',
        },
      },
    },
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            backgroundColor: '#40414f',
            '& fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.1)',
            },
            '&:hover fieldset': {
              borderColor: 'rgba(255, 255, 255, 0.2)',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#19c37d',
            },
          },
        },
      },
    },
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: '#343541',
          scrollbarWidth: 'thin',
          '&::-webkit-scrollbar': {
            width: '8px',
          },
          '&::-webkit-scrollbar-track': {
            background: '#40414f',
          },
          '&::-webkit-scrollbar-thumb': {
            background: '#565869',
            borderRadius: '4px',
          },
        },
      },
    },
  },
});

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = {
      id: Date.now(),
      text: input.trim(),
      sender: 'user',
      timestamp: new Date().toLocaleTimeString(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setError(null);
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/chat', {
        message: userMessage.text,
        max_length: 100,
        temperature: 0.7
      });

      const botMessage = {
        id: Date.now() + 1,
        text: response.data.response,
        sender: 'bot',
        timestamp: new Date().toLocaleTimeString(),
        processingTime: response.data.processing_time
      };

      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to get response from the bot');
      console.error('Chat error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className="app-container">
        <Container maxWidth="md" className="chat-container">
          <Box className="messages-container">
            {messages.map((message) => (
              <Paper
                key={message.id}
                className={`message-wrapper ${message.sender}-message`}
                elevation={0}
              >
                <div className="message-content">
                  <Avatar className="message-avatar">
                    {message.sender === 'bot' ? <PsychologyIcon /> : <PersonIcon />}
                  </Avatar>
                  <div className="message-bubble">
                    <div className="message-header">
                      <Typography variant="subtitle2" className="message-sender">
                        {message.sender === 'bot' ? 'TheraPeek' : 'You'}
                      </Typography>
                      <Typography variant="caption" className="message-time">
                        {message.timestamp}
                      </Typography>
                    </div>
                    <Typography
                      variant="body1"
                      className={`message-text ${error ? 'error-message' : ''}`}
                      component="div"
                    >
                      <ReactMarkdown>{message.text}</ReactMarkdown>
                    </Typography>
                    {message.processingTime && (
                      <Typography variant="caption" className="processing-time">
                        Response time: {message.processingTime}s
                      </Typography>
                    )}
                  </div>
                </div>
              </Paper>
            ))}
            {isLoading && (
              <Paper className="message-wrapper bot-message" elevation={0}>
                <div className="message-content">
                  <Avatar className="message-avatar">
                    <PsychologyIcon />
                  </Avatar>
                  <div className="message-bubble">
                    <Box display="flex" alignItems="center" gap={1}>
                      <CircularProgress size={20} />
                      <Typography>Thinking...</Typography>
                    </Box>
                  </div>
                </div>
              </Paper>
            )}
            {error && (
              <Paper className="message-wrapper bot-message" elevation={0}>
                <div className="message-content">
                  <Avatar className="message-avatar">
                    <PsychologyIcon />
                  </Avatar>
                  <div className="message-bubble">
                    <Typography color="error">{error}</Typography>
                  </div>
                </div>
              </Paper>
            )}
            <div ref={messagesEndRef} />
          </Box>
          <Box
            component="form"
            onSubmit={handleSubmit}
            className="input-container"
            sx={{ p: 2 }}
          >
            <TextField
              fullWidth
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message here..."
              variant="outlined"
              disabled={isLoading}
              InputProps={{
                endAdornment: (
                  <Button
                    type="submit"
                    disabled={isLoading || !input.trim()}
                    sx={{ ml: 1 }}
                  >
                    {isLoading ? (
                      <CircularProgress size={24} />
                    ) : (
                      <SendIcon />
                    )}
                  </Button>
                ),
              }}
            />
          </Box>
        </Container>
      </div>
    </ThemeProvider>
  );
};

export default App;
